import { CHANGE_SONG, ChangeSongAction, Song } from "../types";

export const currentlyPlaying = (
  state: Song | {} = {},
  action: ChangeSongAction
) => {
  switch (action.type) {
    case CHANGE_SONG: {
      return action.payload;
    }
    default: {
      return state;
    }
  }
};
