import React, { FC, ReactElement, useState } from "react";

interface AccordionProps {
  title: string;
}

const Accordion: FC<AccordionProps> = ({ title }): ReactElement => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div
      style={{
        width: "100%",
        display: "flex",
        alignItems: "center",
        fontSize: "48px",
        padding: "2vh 0"
      }}
    >
      {title}
    </div>
  );
};

export default Accordion;
