import axios, { AxiosResponse } from "axios";
import { ActionCreator, Dispatch } from "redux";
import {
  BasicDetails,
  FETCH_ALBUMS,
  FETCH_ALBUMS_FAILURE,
  FETCH_ALBUMS_SUCCESS,
  FetchAlbumsAction,
  FetchAlbumsFailureAction,
  FetchAlbumsSuccessAction,
  Song
} from "../types";

const fetchAlbumsAction: ActionCreator<FetchAlbumsAction> = () => ({
  type: FETCH_ALBUMS
});

const fetchAlbumsFailure: ActionCreator<FetchAlbumsFailureAction> = (
  error
) => ({
  type: FETCH_ALBUMS_FAILURE,
  payload: { error }
});

const fetchAlbumsSuccess: ActionCreator<FetchAlbumsSuccessAction> = (
  albums
) => ({
  type: FETCH_ALBUMS_SUCCESS,
  payload: albums
});

export const fetchAlbums = () => {
  return (dispatch: Dispatch) => {
    dispatch(fetchAlbumsAction());

    axios
      .get(`/api/albums`)
      .then((albumResponse: AxiosResponse<Song[]>) => {
        Promise.all(
          albumResponse.data
            .map((album: BasicDetails) =>
              axios
                .get(`/api/songs/album/${album._id}`)
                .then((songResponse) => ({ [album._id]: songResponse.data }))
            )
        )
          .then((songs) => {
            console.log(songs.join())
            let albumsData = albumResponse.data.reduce(
              (acc: {}, { _id, name }: Song) => {
                console.log(_id);
                acc = {
                  ...acc,
                  //@ts-ignore
                  [_id]: { name, songs: songs[_id] }
                };
                return acc;
              },
              {}
            );
            dispatch(fetchAlbumsSuccess(albumsData));
          })
          .catch((err) => {
            dispatch(fetchAlbumsFailure(err.message));
          });
      })
      .catch((err) => {
        dispatch(fetchAlbumsFailure(err.message));
      });
  };
};
