import React, { ReactElement } from "react";
import { Action } from "redux";
import { connect, ConnectedProps } from "react-redux";
import { ThunkDispatch } from "redux-thunk";
import IdleTimer from "react-idle-timer";
// @ts-ignore TODO: create type file for this library
import { Menu, MusicPlayer, PageLoader } from "./views";
import {
  fetchAlbums,
  fetchArtists,
  fetchPlaylists,
  fetchSongs
} from "./store/actions";
import { State } from "./store/types";
import { SongMenu } from "./components/Menu";

const mapDispatchToProps = (dispatch: ThunkDispatch<State, void, Action>) => ({
  fetchAlbums: () => dispatch(fetchAlbums()),
  fetchArtists: () => dispatch(fetchArtists()),
  fetchPlaylists: () => dispatch(fetchPlaylists()),
  fetchSongs: () => dispatch(fetchSongs())
});

const connector = connect(null, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

const App: React.FC<PropsFromRedux> = ({
  fetchAlbums,
  fetchArtists,
  fetchPlaylists,
  fetchSongs
}): ReactElement => {
  fetchAlbums();
  fetchArtists();
  fetchPlaylists();
  fetchSongs();

  return (
    <>
      <div
        style={{
          backgroundColor: "#cdcdcd",
          width: "100vw",
          height: "100vh",
          fontFamily: "'Work Sans', sans-serif",
          overflow: "hidden"
        }}
      >
        <IdleTimer
          timeout={4000}
          onActive={() => console.log(false)}
          onIdle={() => console.log(true)}
          debounce={250}
        />
        <SongMenu />
        <PageLoader />
        <MusicPlayer />
        <Menu />
      </div>
    </>
  );
};

export default connector(App);
