import { CHANGE_SONG, ChangeSongAction } from "../types";
import { MUSIC_REGISTRY } from "../../musicRegistry";

export const currentlyPlaying = (
  state: MUSIC_REGISTRY = MUSIC_REGISTRY.HEY,
  action: ChangeSongAction
) => {
  switch (action.type) {
    case CHANGE_SONG: {
      return action.payload;
    }
    default: {
      return state;
    }
  }
};
